testTemplate:
from wasabi import msg
from black import format_str, FileMode

file_mode = FileMode()

def blacken(code):
    try:
        return format_str(code, mode=file_mode)
    except:
        return code

__msg__ = msg
__solution__ = blacken("""${solution}""")

${solution}

${test}

try:
    test()
except AssertionError as e:
    __msg__.fail(e)

